import React, {Component} from 'react';


class SkillColumn extends Component {

        constructor(props) {
            super(props);


            this.state = {
                canvas: null,
                context: null
            };

        }

        setCanvas() {
            const canvas = document.getElementsByClassName('skill-column skill-pct');
            var context = null;

            if (canvas) {
                context = canvas.getContext('2d');
            }

            this.setState({
                canvas: canvas,
                context: context
            });
        }

        animate(current) {
        var x = this.canvas.width / 2;
        var y = this.canvas.height / 2;
        var radius = 75;
        var endPercent = 85;
        var curPerc = 0;
        var circ = Math.PI * 2;
        var quart = Math.PI / 2;

        this.context.clearRect(0, 0, this.canvas.width, this.canvas.height);
        this.context.beginPath();
        this.context.arc(x, y, radius, -(quart), ((circ) * current) - quart, false);
        this.context.stroke();
        curPerc++;
        if (curPerc < endPercent) {
            requestAnimationFrame(function () {
                this.animate(curPerc / 100)
            });
        }
    }

    componentDidMount() {
        console.log('componentDidMount()');

        this.setCanvas();

        this.context.lineWidth = 10;
        this.context.strokeStyle = '#ad2323';
        this.context.shadowOffsetX = 0;
        this.context.shadowOffsetY = 0;
        this.context.shadowBlur = 10;
        this.context.shadowColor = '#656565';

        this.animate();

    }

    render() {
        console.log('render()')
        const thisClassName = "fa p-3 " + this.props.icon + " fa-2x";

        return(
            <React.Fragment>
                <div className="col-lg-2 col-md-4 col-sm-6 px-1 mb-5">
                    <div className="skill-column text-center">
                        <canvas className="skill-pct">{this.props.pct}%</canvas>
                        <h4 className="skill-title mt-4">{this.props.title}</h4>
                        <p className="skill-description px-2">{this.props.description}</p>
                        <div className="skill-icon">
                            <i className={thisClassName}></i>
                        </div>
                    </div>
                </div>

            </React.Fragment>

        );
    }


}

export default SkillColumn;
