import React, {Component} from 'react';


class SkillColumn extends Component {

    animate(current) {
        var x = this.props.canvas.width / 2;
        var y = this.props.canvas.height / 2;
        var radius = 75;
        var endPercent = 85;
        var curPerc = 0;
        var circ = Math.PI * 2;
        var quart = Math.PI / 2;

        this.props.context.clearRect(0, 0, this.props.canvas.width, this.props.canvas.height);
        this.props.context.beginPath();
        this.props.context.arc(x, y, radius, -(quart), ((circ) * current) - quart, false);
        this.props.context.stroke();
        curPerc++;
        if (curPerc < endPercent) {
            requestAnimationFrame(function () {
                this.animate(curPerc / 100)
            });
        }
    }

    componentDidMount() {
        console.log('skillColumn.js');

        this.props.canvas = document.getElement('skill-column skill-pct');
        this.props.context = this.props.canvas.getContext('2d');


        this.props.context.lineWidth = 10;
        this.props.context.strokeStyle = '#ad2323';
        this.props.context.shadowOffsetX = 0;
        this.props.context.shadowOffsetY = 0;
        this.props.context.shadowBlur = 10;
        this.props.context.shadowColor = '#656565';

        this.animate();

    }

    render() {
        const thisClassName = "fa p-3 " + this.props.icon + " fa-2x";

        return(
            <React.Fragment>
                <div className="col-lg-2 col-md-4 col-sm-6 px-1 mb-5">
                    <div className="skill-column text-center">
                        <canvas className="skill-pct">{this.props.pct}%</canvas>
                        <h4 className="skill-title mt-4">{this.props.title}</h4>
                        <p className="skill-description px-2">{this.props.description}</p>
                        <div className="skill-icon">
                            <i className={thisClassName}></i>
                        </div>
                    </div>
                </div>

            </React.Fragment>

        );
    }


}

export default SkillColumn;
