import React, {Suspense} from 'react';
import {useImage} from 'react-image'
import {Img} from 'react-image'
import Loading from './Loading';

const LazyImage = (props) => {
    const {src} = useImage({
        srcList: props.src,
      })

    return(
        <Suspense fallback={<h1>Help me lord...</h1>}>
            <Img src={src}
                  loader={Loading}
                  alt={props.alt} />
        </Suspense>
    );
}

export default LazyImage;
