import React, { Component } from "react";
import { backendUrl } from '../shared/urls';


class PortfolioGrid extends Component {
  state = {
    loading: false,
    data: [],
    headline: []
  };

  componentDidMount() {
    this.setState({ loading: true });

    fetch(backendUrl + "portfolio?_embed")
      .then(specialties => specialties.json())
      .then(specialties =>

        this.setState({ data: specialties, loading: false }, () =>
          console.log("data loaded")
        )

      );
  }

  render() {
    return (
        <div className="container">
          {this.state.loading ? (
            "loading..."
          ) : (
            <div id="portfolio-grid">
              <div className="row m-5 text-center">
                {this.state.data.map((specialty, indx) => {
                  return (
                    <div className="col-md m-1 portfolio-item" key={indx}>
                      <img
                        style={{ "width": "100%"}}
                        src={specialty._embedded["wp:featuredmedia"][0].source_url}
                        alt={specialty.slug}
                      />
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
    );
  }
}

export default PortfolioGrid;
