import React from 'react';
import RenderPageTitle from '../../components/pagetitle/pageTitleComponent';
import Loading from '../../components/Loading';
import unescapedString from '../../shared/unescapedString';

import './styles.css';


const PortfolioDetail = (props) => {

    if (props.post.isLoading) {
        return(
            <div className="">
                <div className="row">
                    <Loading />
                </div>
            </div>
        );
    }
    else if (props.post.errMess) {
        return(
            <div className="">
                <div className="row">
                    <h4>{props.errMess}</h4>
                </div>
            </div>
        );
    }
    else if (props.post != null) {

        if (props.images.isLoading) {
            consoles.log('images are loading');
        } else if (props.images.errMess) {
            consoles.log('images error:' + props.images.errMess);
        } else if (props.images != null) {
            console.log('images:');
            console.log(props.images)
        }

        function innerHTML(selector) {

            if (selector) {
                return selector.innerHTML;
            }
            return null;
        }


        const rawContent = props.post.content.rendered;

        var parser = new DOMParser();
        var doc = parser.parseFromString(rawContent, "text/html");

        var description = innerHTML(doc.querySelector(".description"));
        var images = innerHTML(doc.querySelector(".images"));
        var links = innerHTML(doc.querySelector(".links"));
        var gallery = innerHTML(doc.querySelector(".gallery"));
        console.log("images");
        console.log(props.images);
        return(
            <React.Fragment>
                <div className="project-page">
                    <div className="site-page ">
                        <RenderPageTitle theme="light" icon="fa-briefcase" title="PROJECT" boxed_title="Detail" />
                        <div className="row">
                            <div className="col">
                                {description}

                                {props.images.map((post, indx) => {
                                        return(
                                            <React.Fragment>
                                                <div key={indx}>
                                                    {post.source_url}
                                                </div>
                                            </React.Fragment>
                                        );
                                })

                                }
                        </div>
                    </div>
                    </div>
                </div>              
            </React.Fragment>
        );
    } 
}

export default PortfolioDetail;
