import React from 'react';
import RenderPageTitle from '../../components/pagetitle/pageTitleComponent';
import RecommendationsCarousel from './recommendationsCarousel';
import {wpGetFeaturedImage} from '../../shared/wpGetFeaturedImage';
import './styles.css';

const Clients = (props) => {

    return(
        <React.Fragment>
            <div className="recommendations-page">
                <div className="site-page ">
                    <RenderPageTitle theme="dark" icon="fa-users" title="MY" boxed_title="CLIENTS" />
                    <RecommendationsCarousel recommendations={props.recommendations} />
                    <div className="row m-0 p-0">
                        <div className="col-lg-4 col-md-6">
                            {props.clients.map((client, indx) => {
                                var item_url = wpGetFeaturedImage(client, null);
                                const background_url = "url('" + item_url + "')";
                                const item_style = {
                                    "background-image": background_url
                                }

                                return (
                                    <div className="client-item" key={indx} style={item_style} />
                                );
                            })}

                        </div>
                    </div>
                </div>
            </div>              

        </React.Fragment>
    );

}

export default Clients;
