import React, { Component } from 'react';
import { Jumbotron} from 'reactstrap';
import { Fade } from 'react-animation-components';
import LogoCube from '../../components/logoCube/Component';
import './styles.css';
import './cool-backgrounds.css'

class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            shouldRender: false,
            repaintDelay: null
          };        
    }

    componentDidMount() {
        var self = this;
        var myTimeout = setTimeout(function() {
            self.setState({shouldRender: true});
        }, 50);    
        this.setState({repaintDelay: myTimeout});
    }

    shouldComponentUpdate(nextProps, nextState) {
        console.log("shouldComponentUpdate()", this.state.shouldRender);
        return this.state.shouldRender;
    }

    componentWillUnmount() {
        /* kill any pending background threads that were
        invoked in componentDidMount(). */
        clearTimeout(this.state.repaintDelay);
    }

    render() {
        return(
            <div key="home-page">
            {(!this.state.shouldRender) ? (
                <div className="mt-5 pt-5">
                    Loading...
                </div>
            ) : (
                <Jumbotron className="dark-background">
                <div className="h-100">
                    <div className="row ml-0 mr-0 px-0 jumbotron-spacer">
                        <div className="col-lg-6 col-md-12 text-center noselect ">
                            <h1 className="display-4">
                                <span className="px-3 super-bold pre-wrap">Lawrence McDaniel</span>
                            </h1>
                            <Fade in> 
                                <p className="jumbotron-subtitle pre-wrap">Full Stack Developer</p>
                            </Fade>
                        </div>
                        <div className="col-lg-6 col-md-12">
                            <LogoCube logos={this.props.logos} />
                        </div>
                    </div>
                </div>                    
            </Jumbotron>

                )}                
            </div>


        );
    }
}
export default Home;
