import React, { Component } from "react";
import { Link } from 'react-router-dom';
import Loading from '../../components/Loading';

/*
https://www.w3schools.com/howto/tryit.asp?filename=tryhow_css_image_overlay_slideleft
*/
class ProjectsGrid extends Component {


  render() {

    function RenderProjectItem({ project }) {
      console.log('RenderProjectItem()');
      const item_url = project._embedded['wp:featuredmedia'][0].source_url;
      const background_url = "url('" + item_url + "')";
      const item_style = {
        "backgroundImage": background_url
      }
      return (
        <Link key={project.id} to={`/portfolio/${project.id}`} >
          <div className="col-lg-3 col-md-4 col-sm-6 m-0 p-0 noselect">
              <div className="portfolio-item">
                <div className="portfolio-item-image"
                    style={item_style}>
                </div>
                <div className="portfolio-item-overlay" >
                  {project.title.rendered}
                </div>
              </div>
          </div>
        </Link>
      );

  }
    return (
        <React.Fragment>
          {this.props.isLoading ? (
            <Loading />
          ) : (
            <div id="portfolio-grid">
              <div className="row my-5 mx-0 py-5 px-0 text-center">
                  {this.props.portfolio.projects.map((portfolio_item) => {
                    return(
                        <RenderProjectItem project={portfolio_item} />
                    );
                  })}
              </div>
            </div>
            )
          }
        </React.Fragment>
    );
  }
}

export default ProjectsGrid;
