import React, { Component } from 'react';
import Header from './HeaderComponent';
import Footer from './FooterComponent';
import Home from './HomeComponent';
import Contact from './ContactComponent';
import About from './AboutPage';
import Bio from './BioPage';
import Specialties from './SpecialtiesPage';
import Portfolio from './PortfolioPage';
import Education from './EducationPage';
import Recommendations from './RecommendationsPage';
import { Switch, Route, Redirect, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';


import Menu from './MenuComponent';
import DishDetail from './DishdetailComponent';
/*
import { postComment, postFeedback, 
  fetchDishes, fetchComments, fetchPromos, fetchLeaders, fetchSpecialties } from '../redux/ActionCreators';
  */
import { fetchSpecialties } from '../redux/ActionCreators';
import { actions } from 'react-redux-form';


const mapStateToProps = state => {
  return {
    specialties: state.specialties
    /*
    dishes: state.dishes,
    comments: state.comments,
    promotions: state.promotions,
    leaders: state.leaders
    */
  }
}

const mapDispatchToProps = (dispatch) => ({
  /*
  postComment: (dishId, rating, author, comment) => dispatch(postComment(dishId, rating, author, comment)),
  postFeedback: (firstname, lastname, telnum, email, agree, message) => dispatch(postFeedback(firstname, lastname, telnum, email, agree, message)),
  fetchDishes: () => {dispatch(fetchDishes())},
  fetchComments: () => {dispatch(fetchComments())},
  fetchPromos: () => {dispatch(fetchPromos())},
  fetchLeaders: () => {dispatch(fetchLeaders())},
  resetFeedbackForm: () => {dispatch(actions.reset('feedback'))},
  */
  fetchSpecialties: () => {dispatch(fetchSpecialties())}
});

class Main extends Component {

  componentDidMount() {
    this.props.fetchSpecialties();
    /* 
    this.props.fetchDishes();
    this.props.fetchComments();
    this.props.fetchPromos();
    this.props.fetchLeaders();
    */
  }

  render() {

    const HomePage = () => {
      return(
        <Home />
      );
    }


    const DishWithId = ({match}) => {
      return(
        <DishDetail dish={this.props.dishes.dishes.filter((dish) => dish.id === parseInt(match.params.dishId, 10))[0]} 
            isLoading={this.props.dishes.isLoading}
            errMess={this.props.dishes.errMess}
            comments={this.props.comments.comments.filter((comment) => comment.dishId === parseInt(match.params.dishId, 10))}
            commentsErrMess={this.props.comments.errMess}
            postComment={this.props.postComment}
        />
      );
    }

    return ( 
      <div>
        <Header />
          <Switch>
            <Route path="/home" component={HomePage} />
            <Route exact path="/menu" component={() => <Menu dishes={this.props.dishes} />} />
            <Route path="/menu/:dishId" component={DishWithId} />
            <Route exact path="/contact" component={() => <Contact 
                                                              resetFeedbackForm={this.props.resetFeedbackForm} 
                                                              postFeedback={this.props.postFeedback}
                                                              />
                                                      } />
            <Route exact path="/about" component={About} />
            <Route exact path="/bio" component={Bio} />
            <Route exact path="/specialties" component={Specialties} />
            <Route exact path="/portfolio" component={Portfolio} />
            <Route exact path="/education" component={Education} />
            <Route exact path="/recommendations" component={Recommendations} />
            <Redirect to="/home" />
          </Switch>
        <Footer />
      </div>
    )
  }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Main));
