import React, { Component } from 'react';
import { Card, CardImg, CardText, CardBody,
    CardTitle } from 'reactstrap';

class DishDetail extends Component {

    constructor(props) {
        super(props);

        console.log("Menu DishDetail constructor is invoked")
        this.setState({ dish: props.dish});
    }

    renderDish(dish) {
        console.log("Menu DishDetail renderDish invoked")
        if (dish != null)
            return(
                <div  className="col-12 col-md-5 m-1">
                    <Card>
                        <CardImg width="100%" top src={dish.image} alt={dish.name} />
                        <CardBody>
                            <CardTitle>{dish.name}</CardTitle>
                            <CardText>{dish.description}</CardText>
                        </CardBody>
                    </Card>
                </div>
);
        else
            return(
                <div></div>
            );
    }

    renderComments(dish) {
        console.log("Menu DishDetail renderComments invoked")

        if (dish = null)
            return ( <div></div> )

        if (dish.comments != null) {
            return (
                <div  className="col-12 col-md-5 m-1">
                    <Card>
                        <CardBody>
                            <CardTitle>Comments</CardTitle>
                            <CardText>{comments}</CardText>
                        </CardBody>
                    </Card>
                </div>

            )

        }
        else 
            return(
                <div></div>
            )
    }

    render() {
        console.log("Menu DishDetail render is invoked")
        const dish = this.props.dish;

        if (dish != null) {
            const comments = this.props.dish.comments.map((comment) => {
                return (
                  <div key={comment.id} className="">
                    <p>{comment.comment}</p>
                    <p>-- {comment.author}, {comment.date}</p>
                  </div>
                );
            });

            return(
                <div className="container">
                    <div className="row">
                        {this.renderDish(this.props.dish)}
                        {this.renderComments(this.props.dish)}
                        <div  className="col-12 col-md-5 m-1">
                            <Card>
                                <CardBody>
                                    <CardTitle>Comments</CardTitle>
                                    <CardText>{comments}</CardText>
                                </CardBody>
                            </Card>

                        </div>
                    </div>
                </div>
            );
        }
        else
            return(
                <div></div>
            );

    }
}

export default DishDetail;
