import React, { Component } from "react";
import { backendUrl } from '../shared/urls';


class PortfolioGrid extends Component {
  state = {
    loading: false,
    data: [],
    headline: []
  };

  componentDidMount() {
    this.setState({ loading: true });

    fetch(backendUrl + "portfolio?_embed")
      .then(specialties => specialties.json())
      .then(specialties =>

        this.setState({ data: specialties, loading: false }, () =>
          console.log("data loaded")
        )

      );
  }

  render() {
    return (
        <div className="container">
          {this.state.loading ? (
            "loading..."
          ) : (
            <div id="portfolio-grid">
              <div className="row my-5 mx-0 p-0 text-center">
                {this.state.data.map((portfolio_item, indx) => {
                  const item_url = portfolio_item._embedded['wp:featuredmedia'][0].source_url;
                  const background_url = "url('" + item_url + "')";
                  const item_style = {
                    "background-image": background_url
                  }
                  return (
                    <div className="col-md-3 portfolio-item m-0 px-0 py-1" key={indx}>
                        <div 
                            className="portfolio-item-internal mx-1 my-0" 
                            style={item_style}>
                        </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
    );
  }
}

export default PortfolioGrid;
