import * as ActionTypes from './ActionTypes';


export const Dishes = (
    state = {
        isLoading: true,
        errMess: null,
        dishes: []
    }, 
    action
    ) => {
    console.log('Dishes switch: ' + action.type);

    switch (action.type) {

        case ActionTypes.ADD_DISHES:
            console.log('Dishes action - ADD_DISHES');
            return {
                ...state, 
                isLoading: false, 
                errMess: null, 
                dishes: action.payload
            };

        case ActionTypes.DISHES_LOADING:
            console.log('Dishes action - DISHES_LOADING');
            return {
                ...state, 
                isLoading: true, 
                errMess: null, 
                dishes: []
            };

        case ActionTypes.DISHES_FAILED:
            console.log('Dishes action - DISHES_FAILED: ' + action.payload);
            return {
                ...state, 
                isLoading: false, 
                errMess: action.payload, 
                dishes: []
            };

        default: 
        console.log('Dishes action - default');
        return state;
    }
};
