import * as ActionTypes from './ActionTypes';


export const CourseGridRedux = (state = {
    isSet: false,
    state: null
    }, action) => {
    switch(action.type) {
        case ActionTypes.SET_COURSEGRID_STATE:
            return {...state, isSet: true, state: action.state};

        default: 
            return state;
    }
};
