export const shuffleArray = array => {

  for (i = 0; i < array.length - 1; i++) {
      var tmp = [];
      var j = 0;
      do {
        j = Math.floor(Math.random() * (i + 1));
      } while (tmp.includes(array[j]))
      tmp[i] = array[j];
    }
    return array;
  }
