export const wpGetFeaturedImage = (post, imageSize) => {

    var image_url = null;

    try {
        image_url = post._embedded['wp:featuredmedia'][0].media_details.sizes[imageSize].source_url;
      }
      catch(err) {
        image_url = post._embedded["wp:featuredmedia"][0].source_url;
      }


    return null;
}
