import React from 'react';
import RenderPageTitle from '../../components/pagetitle/pageTitleComponent';
import Loading from '../../components/Loading';
import unescapedString from '../../shared/unescapedString';

import './styles.css';


const PortfolioDetail = (props) => {

    if (props.isLoading) {
        return(
            <div className="">
                <div className="row">
                    <Loading />
                </div>
            </div>
        );
    }
    else if (props.errMess) {
        return(
            <div className="">
                <div className="row">
                    <h4>{props.errMess}</h4>
                </div>
            </div>
        );
    }
    else if (props.project != null) {

        function innerHTML(selector) {

            if (selector) {
                return selector.innerHTML;
            }
            return null;
        }
        function getURL(postID) {

            fetch('https://lawrencemcdaniel.com/wp-json/wp/v2/media?include[]=2324')
            .then(async response => {
                console.log('getURL() - ' + postID);
                const data = await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response statusText
                    const error = (data && data.message) || response.statusText;
                    return Promise.reject(error);
                }
                console.log(data[0].source_url);
                return data[0].source_url;
            })
            .catch(error => {
                console.error('error:', error);
            });            
        }

        function galleryLinks(idlist) {

            if (idlist) {
                var arr = idlist.split(',');
                let urls;

                for (var i = 0; i < arr.length; i++) {
                    urls += getURL(arr[i]);
                }
                return urls;
            }
            return null;
        }

        const rawContent = props.project.content.rendered;

        var parser = new DOMParser();
        var doc = parser.parseFromString(rawContent, "text/html");

        var description = innerHTML(doc.querySelector(".description"));
        var images = innerHTML(doc.querySelector(".images"));
        var links = innerHTML(doc.querySelector(".links"));
        var gallery = innerHTML(doc.querySelector(".gallery"));

        return(
            <React.Fragment>
                <div className="project-page">
                    <div className="site-page ">
                        <RenderPageTitle theme="light" icon="fa-briefcase" title="PROJECT" boxed_title="Detail" />
                        <div className="row">
                            <div className="col">
                                {description}
                                <br />
                                images:
                                <ul>
                                {images}
                                </ul>
                                <br />
                                links:
                                <ul> 
                                {links}
                                </ul>
                                <br />
                                <div>help me lord.</div>
                                {galleryLinks(gallery)}
                                <div><br /><br /></div>
                            <div className="recommendation-name" dangerouslySetInnerHTML={unescapedString(props.project.content.rendered)} /> 
                        </div>
                    </div>
                    </div>
                </div>              
            </React.Fragment>
        );
    } 
}

export default PortfolioDetail;
