export const shuffleArray = array => {

  var tmp = [];
  for (var i = 0; i < array.length; i++) {
      var j = 0;
      while (tmp.includes(array[j])) {
        j = Math.floor(Math.random() * i);
      }
      console.log("shuffleArray()", i, j)
      tmp.push(array[j]);
    }
    return tmp;
  }
