import React, { Component } from 'react';
import { Navbar, Nav, NavbarToggler, Collapse, NavItem} from 'reactstrap';
import { NavLink } from 'react-router-dom';

class Header extends Component {

    constructor(props) {
        super(props);

        this.state = {
            isNavOpen: false,
            isModalOpen: false
          };

          this.toggleNav = this.toggleNav.bind(this);
        }

    toggleNav() {
        this.setState({
            isNavOpen: !this.state.isNavOpen
        });
    }

    render() {
        return(
            <React.Fragment>
                <Navbar dark expand="md" fixed="top">
                    <div className="container">
                        <NavbarToggler onClick={this.toggleNav} />
                        <Collapse isOpen={this.state.isNavOpen} navbar>
                            <Nav navbar>
                            <NavItem>
                                <NavLink className="nav-link"  to='/'>Home</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link" to='/about'>About</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link"  to='/specialties'>Specialties</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link"  to='/portfolio'>Portfolio</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link"  to='/skills'>Skills</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link"  to='/education'>Education</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link"  to='/testimonials'>Testimonials</NavLink>
                            </NavItem>

                            <NavItem>
                                <NavLink className="nav-link" to='/contact'>Contact</NavLink>
                            </NavItem>

                            <NavItem className="dropdown">
                                <NavLink class="nav-link dropdown-toggle" href="/" id="navbarDropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                Code
                                </NavLink>
                                <div class="dropdown-menu" aria-labelledby="navbarDropdownMenuLink">
                                <NavLink class="dropdown-item" href="/">Action</NavLink>
                                <NavLink class="dropdown-item" href="/">Another action</NavLink>
                                <NavLink class="dropdown-item" href="/">Something else here</NavLink>
                                </div>
                            </NavItem>

                            <NavItem>
                                <NavLink className="nav-link" to='https://blog.lawrencemcdaniel.com/'>Blog</NavLink>
                            </NavItem>

                            </Nav>
                        </Collapse>
                    </div>
                </Navbar>

            </React.Fragment>
        );
    }
}

export default Header;
