import React from 'react';
import RenderPageTitle from '../../components/pagetitle/pageTitleComponent';
import RecommendationsCarousel from './recommendationsCarousel';
import './styles.css';

const Recommendations = (props) => {

    return(
        <React.Fragment>
            <div className="recommendations-page">
                <div className="container site-page ">
                    <RenderPageTitle theme="dark" icon="fa-users" title="CLIENT" boxed_title="RECOMMENDATIONS" />
                    <div>
                    {props.recommendations.items}
                    {props.recommendations.items.map((recommendation, indx) => {
                        return (
                            <div key={indx}>
                                {recommendation._embedded["wp:featuredmedia"][0].source_url}
                                {recommendation.slug}
                            />
                            </div>
                        );
                })}

                    </div>
                </div>
            </div>              

        </React.Fragment>
    );

}

export default Recommendations;
